import{_ as i,c as a,o as n,a as e}from"./app.ea3c2dda.js";const f='{"title":"CSS \u52A8\u753B","description":"","frontmatter":{},"headers":[{"level":2,"title":"2D\u8F6C\u6362","slug":"_2d\u8F6C\u6362"},{"level":2,"title":"3D\u8F6C\u6362","slug":"_3d\u8F6C\u6362"},{"level":2,"title":"\u8FC7\u6E21","slug":"\u8FC7\u6E21"},{"level":2,"title":"\u52A8\u753B","slug":"\u52A8\u753B"},{"level":3,"title":"@keyframes","slug":"keyframes"},{"level":3,"title":"animation-name","slug":"animation-name"},{"level":3,"title":"animation-duration","slug":"animation-duration"},{"level":3,"title":"animation-delay","slug":"animation-delay"},{"level":3,"title":"animation-iteration-count","slug":"animation-iteration-count"},{"level":3,"title":"animation-direction","slug":"animation-direction"},{"level":3,"title":"animation-timing-function","slug":"animation-timing-function"},{"level":3,"title":"animation-fill-mode","slug":"animation-fill-mode"},{"level":3,"title":"animation","slug":"animation"},{"level":3,"title":"animation-play-state","slug":"animation-play-state"}],"relativePath":"cssdocs/cssAnimation.md","lastUpdated":1641832711807}',t={},l=e(`<h1 id="css-\u52A8\u753B" tabindex="-1">CSS \u52A8\u753B <a class="header-anchor" href="#css-\u52A8\u753B" aria-hidden="true">#</a></h1><h2 id="_2d\u8F6C\u6362" tabindex="-1">2D\u8F6C\u6362 <a class="header-anchor" href="#_2d\u8F6C\u6362" aria-hidden="true">#</a></h2><p>CSS \u8F6C\u6362\uFF08transforms\uFF09\u5141\u8BB8\u60A8\u79FB\u52A8\u3001\u65CB\u8F6C\u3001\u7F29\u653E\u548C\u503E\u659C\u5143\u7D20\u3002</p><p>\u901A\u8FC7\u4F7F\u7528 CSS transform \u5C5E\u6027\uFF0C\u60A8\u53EF\u4EE5\u5229\u7528\u4EE5\u4E0B 2D \u8F6C\u6362\u65B9\u6CD5\uFF1A</p><ul><li>translate() \u4ECE\u5176\u5F53\u524D\u4F4D\u7F6E\u79FB\u52A8\u5143\u7D20\uFF08\u6839\u636E\u4E3A X \u8F74\u548C Y \u8F74\u6307\u5B9A\u7684\u53C2\u6570\uFF09\u3002</li><li>rotate() \u6839\u636E\u7ED9\u5B9A\u7684\u89D2\u5EA6\u987A\u65F6\u9488\u6216\u9006\u65F6\u9488\u65CB\u8F6C\u5143\u7D20\u3002</li><li>scaleX() \u589E\u52A0\u6216\u51CF\u5C11\u5143\u7D20\u7684\u5BBD\u5EA6\u3002</li><li>scaleY() \u589E\u52A0\u6216\u51CF\u5C11\u5143\u7D20\u7684\u9AD8\u5EA6\u3002</li><li>scale() \u589E\u52A0\u6216\u51CF\u5C11\u5143\u7D20\u7684\u5927\u5C0F\uFF08\u6839\u636E\u7ED9\u5B9A\u7684\u5BBD\u5EA6\u548C\u9AD8\u5EA6\u7684\u500D\u6570\uFF09\u3002</li><li>skewX() \u4F7F\u5143\u7D20\u6CBF X \u8F74\u503E\u659C\u7ED9\u5B9A\u89D2\u5EA6\u3002</li><li>skewY() \u4F7F\u5143\u7D20\u6CBF Y \u8F74\u503E\u659C\u7ED9\u5B9A\u89D2\u5EA6\u3002</li><li>skew() \u4F7F\u5143\u7D20\u6CBF X \u548C Y \u8F74\u503E\u659C\u7ED9\u5B9A\u89D2\u5EA6\u3002</li><li>matrix() \u628A\u6240\u6709 2D \u53D8\u6362\u65B9\u6CD5\u7EC4\u5408\u4E3A\u4E00\u4E2A\u3002\u53EF\u63A5\u53D7\u516D\u4E2A\u53C2\u6570\uFF0C \u53C2\u6570\u5982\u4E0B\uFF1Amatrix(scaleX(),skewY(),skewX(),scaleY(),translateX(),translateY())</li></ul><h4 id="transform-origin" tabindex="-1">transform-origin <a class="header-anchor" href="#transform-origin" aria-hidden="true">#</a></h4><p>transform-origin \u5C5E\u6027\u5141\u8BB8\u60A8\u6539\u53D8\u88AB\u8F6C\u6362\u5143\u7D20\u7684\u4F4D\u7F6E\u3002</p><p>\u8BED\u6CD5</p><div class="language-"><pre><code>transform-origin: x-axis y-axis z-axis;
</code></pre></div><ul><li>x-axis \u5B9A\u4E49\u89C6\u56FE\u88AB\u7F6E\u4E8E X \u8F74\u7684\u4F55\u5904\u3002</li><li>y-axis \u5B9A\u4E49\u89C6\u56FE\u88AB\u7F6E\u4E8E Y \u8F74\u7684\u4F55\u5904\u3002</li><li>z-axis \u5B9A\u4E49\u89C6\u56FE\u88AB\u7F6E\u4E8E Z \u8F74\u7684\u4F55\u5904\u3002</li></ul><p>\u53EF\u80FD\u7684\u503C\uFF1A left \u3001center \u3001right \u3001length \u3001%</p><p>\u9ED8\u8BA4\u503C\uFF1A 50% 50% 0</p><h2 id="_3d\u8F6C\u6362" tabindex="-1">3D\u8F6C\u6362 <a class="header-anchor" href="#_3d\u8F6C\u6362" aria-hidden="true">#</a></h2><p>\u901A\u8FC7 CSS transform \u5C5E\u6027\uFF0C\u60A8\u53EF\u4EE5\u4F7F\u7528\u4EE5\u4E0B 3D \u8F6C\u6362\u65B9\u6CD5\uFF1A</p><ul><li>matrix3d(n,n,n,n,n,n, n,n,n,n,n,n,n,n,n,n) \u5B9A\u4E49 3D \u8F6C\u6362\uFF0C\u4F7F\u7528 16 \u4E2A\u503C\u7684 4x4 \u77E9\u9635\u3002</li><li>translate3d(x,y,z) \u5B9A\u4E49 3D \u8F6C\u5316\u3002\u53EF\u5355\u72EC\u62C6\u5206translateX(x)\u3001translateY(y)\u3001translateZ(z)</li><li>scale3d(x,y,z) \u5B9A\u4E49 3D \u7F29\u653E\u8F6C\u6362\u3002scaleX(x)\u3001scaleY(y)\u3001scaleZ(z)</li><li>rotate3d(x,y,z,angle) \u5B9A\u4E49 3D \u65CB\u8F6C\u3002 rotateX()\u3001rotateY()\u3001rotateZ()</li><li>perspective(n) \u5B9A\u4E49 3D \u8F6C\u6362\u5143\u7D20\u7684\u900F\u89C6\u89C6\u56FE\u3002</li><li>perspective-origin \u89C4\u5B9A 3D \u5143\u7D20\u7684\u5E95\u90E8\u4F4D\u7F6E\u3002</li><li>backface-visibility \u5B9A\u4E49\u5143\u7D20\u5728\u4E0D\u9762\u5BF9\u5C4F\u5E55\u65F6\u662F\u5426\u53EF\u89C1\u3002</li><li>transform-style \u89C4\u5B9A\u88AB\u5D4C\u5957\u5143\u7D20\u5982\u4F55\u5728 3D \u7A7A\u95F4\u4E2D\u663E\u793A\u3002</li><li>transform-origin \u5141\u8BB8\u4F60\u6539\u53D8\u88AB\u8F6C\u6362\u5143\u7D20\u7684\u4F4D\u7F6E\u3002</li></ul><h2 id="\u8FC7\u6E21" tabindex="-1">\u8FC7\u6E21 <a class="header-anchor" href="#\u8FC7\u6E21" aria-hidden="true">#</a></h2><p>CSS \u8FC7\u6E21\u5141\u8BB8\u60A8\u5728\u7ED9\u5B9A\u7684\u65F6\u95F4\u5185\u5E73\u6ED1\u5730\u6539\u53D8\u5C5E\u6027\u503C\u3002</p><ul><li>transition: property duration timing-function delay; \u7B80\u5199\u5C5E\u6027\uFF0C\u7528\u4E8E\u5C06\u56DB\u4E2A\u8FC7\u6E21\u5C5E\u6027\u8BBE\u7F6E\u4E3A\u5355\u4E00\u5C5E\u6027\u3002</li><li>transition-delay:time; \u89C4\u5B9A\u8FC7\u6E21\u6548\u679C\u7684\u5EF6\u8FDF\uFF08\u4EE5\u79D2\u8BA1\uFF09\u3002</li><li>transition-duration:time; \u89C4\u5B9A\u8FC7\u6E21\u6548\u679C\u8981\u6301\u7EED\u591A\u5C11\u79D2\u6216\u6BEB\u79D2\u3002</li><li>transition-property: none|all|property; \u89C4\u5B9A\u8FC7\u6E21\u6548\u679C\u6240\u9488\u5BF9\u7684 CSS \u5C5E\u6027\u7684\u540D\u79F0\u3002</li><li>transition-timing-function: linear\uFF08\u76F8\u540C\u7684\u901F\u5EA6\uFF09|ease\uFF08\u6162\u5757\u6162\uFF09|ease-in\uFF08\u6162\u901F\u5F00\u59CB\uFF09|ease-out\uFF08\u6162\u901F\u7ED3\u675F\uFF09|ease-in-out\uFF08\u6162\u901F\u5F00\u59CB\u548C\u7ED3\u675F\uFF09|cubic-</li><li>bezier(n,n,n,n)\uFF08\u4E09\u6B21\u8D1D\u585E\u5C14\u51FD\u6570\u4E2D\u5B9A\u4E49\u81EA\u5DF1\u7684\u503C\uFF0C\u503C\u662F 0 \u81F3 1 \u4E4B\u95F4\uFF09; \u89C4\u5B9A\u8FC7\u6E21\u6548\u679C\u7684\u901F\u5EA6\u66F2\u7EBF\u3002</li></ul><h2 id="\u52A8\u753B" tabindex="-1">\u52A8\u753B <a class="header-anchor" href="#\u52A8\u753B" aria-hidden="true">#</a></h2><p>CSS \u53EF\u5B9E\u73B0 HTML \u5143\u7D20\u7684\u52A8\u753B\u6548\u679C\uFF0C\u800C\u4E0D\u4F7F\u7528 JavaScript \u6216 Flash\uFF01</p><p>\u52A8\u753B\u4F7F\u5143\u7D20\u9010\u6E10\u4ECE\u4E00\u79CD\u6837\u5F0F\u53D8\u4E3A\u53E6\u4E00\u79CD\u6837\u5F0F\u3002</p><p>\u60A8\u53EF\u4EE5\u968F\u610F\u66F4\u6539\u4EFB\u610F\u6570\u91CF\u7684 CSS \u5C5E\u6027\u3002</p><p>\u5982\u9700\u4F7F\u7528 CSS \u52A8\u753B\uFF0C\u60A8\u5FC5\u987B\u9996\u5148\u4E3A\u52A8\u753B\u6307\u5B9A\u4E00\u4E9B\u5173\u952E\u5E27\u3002</p><p>\u5173\u952E\u5E27\u5305\u542B\u5143\u7D20\u5728\u7279\u5B9A\u65F6\u95F4\u6240\u62E5\u6709\u7684\u6837\u5F0F\u3002</p><p>\u5728\u672C\u7AE0\u4E2D\uFF0C\u60A8\u5C06\u5B66\u4E60\u5982\u4E0B\u5C5E\u6027\uFF1A</p><h3 id="keyframes" tabindex="-1">@keyframes <a class="header-anchor" href="#keyframes" aria-hidden="true">#</a></h3><p>\u901A\u8FC7 @keyframes \u89C4\u5219\uFF0C\u60A8\u80FD\u591F\u521B\u5EFA\u52A8\u753B\u3002 \u521B\u5EFA\u52A8\u753B\u7684\u539F\u7406\u662F\uFF0C\u5C06\u4E00\u5957 CSS \u6837\u5F0F\u9010\u6E10\u53D8\u5316\u4E3A\u53E6\u4E00\u5957\u6837\u5F0F\u3002 \u5728\u52A8\u753B\u8FC7\u7A0B\u4E2D\uFF0C\u60A8\u80FD\u591F\u591A\u6B21\u6539\u53D8\u8FD9\u5957 CSS \u6837\u5F0F\u3002</p><p>\u4EE5\u767E\u5206\u6BD4\u6765\u89C4\u5B9A\u6539\u53D8\u53D1\u751F\u7684\u65F6\u95F4\uFF0C\u6216\u8005\u901A\u8FC7\u5173\u952E\u8BCD &quot;from&quot; \u548C &quot;to&quot;\uFF0C\u7B49\u4EF7\u4E8E 0% \u548C 100%\u3002 0% \u662F\u52A8\u753B\u7684\u5F00\u59CB\u65F6\u95F4\uFF0C100% \u52A8\u753B\u7684\u7ED3\u675F\u65F6\u95F4\u3002</p><p>\u8BED\u6CD5</p><div class="language-"><pre><code>@keyframes animationname {keyframes-selector {css-styles;}}
</code></pre></div><table><thead><tr><th>\u503C</th><th>\u63CF\u8FF0</th></tr></thead><tbody><tr><td>animationname</td><td>\u5FC5\u9700\u3002\u5B9A\u4E49\u52A8\u753B\u7684\u540D\u79F0\u3002</td></tr><tr><td>keyframes-selector</td><td>\u5FC5\u9700\u3002\u52A8\u753B\u65F6\u957F\u7684\u767E\u5206\u6BD4\u3002\u5408\u6CD5\u7684\u503C\uFF1A\uFF080-100%\uFF09\uFF08from\uFF08\u4E0E 0% \u76F8\u540C\uFF09to\uFF08\u4E0E 100% \u76F8\u540C\uFF09\uFF09</td></tr><tr><td>css-styles</td><td>\u5FC5\u9700\u3002\u4E00\u4E2A\u6216\u591A\u4E2A\u5408\u6CD5\u7684 CSS \u6837\u5F0F\u5C5E\u6027\u3002</td></tr></tbody></table><h3 id="animation-name" tabindex="-1">animation-name <a class="header-anchor" href="#animation-name" aria-hidden="true">#</a></h3><p>\u89C4\u5B9A @keyframes \u52A8\u753B\u7684\u540D\u79F0\u3002</p><h3 id="animation-duration" tabindex="-1">animation-duration <a class="header-anchor" href="#animation-duration" aria-hidden="true">#</a></h3><p>animation-duration \u5C5E\u6027\u5B9A\u4E49\u52A8\u753B\u5B8C\u6210\u4E00\u4E2A\u5468\u671F\u6240\u9700\u8981\u7684\u65F6\u95F4\uFF0C\u4EE5\u79D2\u6216\u6BEB\u79D2\u8BA1\u3002\u9ED8\u8BA4\u503C\u662F 0\uFF0C\u610F\u5473\u7740\u6CA1\u6709\u52A8\u753B\u6548\u679C\u3002</p><h3 id="animation-delay" tabindex="-1">animation-delay <a class="header-anchor" href="#animation-delay" aria-hidden="true">#</a></h3><p>animation-delay \u5C5E\u6027\u89C4\u5B9A\u52A8\u753B\u5F00\u59CB\u7684\u5EF6\u8FDF\u65F6\u95F4\u3002</p><p>\u8D1F\u503C\u4E5F\u662F\u5141\u8BB8\u7684\u3002\u5982\u679C\u4F7F\u7528\u8D1F\u503C\uFF0C\u5219\u52A8\u753B\u5C06\u5F00\u59CB\u64AD\u653E\uFF0C\u5982\u540C\u5DF2\u64AD\u653E N \u79D2\u3002</p><h3 id="animation-iteration-count" tabindex="-1">animation-iteration-count <a class="header-anchor" href="#animation-iteration-count" aria-hidden="true">#</a></h3><p>animation-iteration-count: n|infinite; \u6307\u5B9A\u52A8\u753B\u5E94\u8FD0\u884C\u7684\u6B21\u6570\u3002</p><ul><li>n \u5B9A\u4E49\u52A8\u753B\u64AD\u653E\u6B21\u6570\u7684\u6570\u503C\u3002</li><li>infinite \u89C4\u5B9A\u52A8\u753B\u5E94\u8BE5\u65E0\u9650\u6B21\u64AD\u653E\u3002</li></ul><h3 id="animation-direction" tabindex="-1">animation-direction <a class="header-anchor" href="#animation-direction" aria-hidden="true">#</a></h3><p>animation-direction \u5C5E\u6027\u6307\u5B9A\u662F\u5411\u524D\u64AD\u653E\u3001\u5411\u540E\u64AD\u653E\u8FD8\u662F\u4EA4\u66FF\u64AD\u653E\u52A8\u753B\u3002</p><ul><li>normal - \u52A8\u753B\u6B63\u5E38\u64AD\u653E\uFF08\u5411\u524D\uFF09\u3002\u9ED8\u8BA4\u503C</li><li>reverse - \u52A8\u753B\u4EE5\u53CD\u65B9\u5411\u64AD\u653E\uFF08\u5411\u540E\uFF09</li><li>alternate - \u52A8\u753B\u5148\u5411\u524D\u64AD\u653E\uFF0C\u7136\u540E\u5411\u540E</li><li>alternate-reverse - \u52A8\u753B\u5148\u5411\u540E\u64AD\u653E\uFF0C\u7136\u540E\u5411\u524D</li></ul><h3 id="animation-timing-function" tabindex="-1">animation-timing-function <a class="header-anchor" href="#animation-timing-function" aria-hidden="true">#</a></h3><p>animation-timing-function \u5C5E\u6027\u89C4\u5B9A\u52A8\u753B\u7684\u901F\u5EA6\u66F2\u7EBF\u3002</p><ul><li>ease - \u6307\u5B9A\u4ECE\u6162\u901F\u5F00\u59CB\uFF0C\u7136\u540E\u52A0\u5FEB\uFF0C\u7136\u540E\u7F13\u6162\u7ED3\u675F\u7684\u52A8\u753B\uFF08\u9ED8\u8BA4\uFF09</li><li>linear - \u89C4\u5B9A\u4ECE\u5F00\u59CB\u5230\u7ED3\u675F\u7684\u901F\u5EA6\u76F8\u540C\u7684\u52A8\u753B</li><li>ease-in - \u89C4\u5B9A\u6162\u901F\u5F00\u59CB\u7684\u52A8\u753B</li><li>ease-out - \u89C4\u5B9A\u6162\u901F\u7ED3\u675F\u7684\u52A8\u753B</li><li>ease-in-out - \u6307\u5B9A\u5F00\u59CB\u548C\u7ED3\u675F\u8F83\u6162\u7684\u52A8\u753B</li><li>cubic-bezier(n,n,n,n) - \u8FD0\u884C\u60A8\u5728\u4E09\u6B21\u8D1D\u585E\u5C14\u51FD\u6570\u4E2D\u5B9A\u4E49\u81EA\u5DF1\u7684\u503C</li></ul><h3 id="animation-fill-mode" tabindex="-1">animation-fill-mode <a class="header-anchor" href="#animation-fill-mode" aria-hidden="true">#</a></h3><p>\u5728\u4E0D\u64AD\u653E\u52A8\u753B\u65F6\uFF08\u5728\u5F00\u59CB\u4E4B\u524D\uFF0C\u7ED3\u675F\u4E4B\u540E\uFF0C\u6216\u4E24\u8005\u90FD\u7ED3\u675F\u65F6\uFF09\uFF0Canimation-fill-mode \u5C5E\u6027\u89C4\u5B9A\u76EE\u6807\u5143\u7D20\u7684\u6837\u5F0F\u3002</p><ul><li>none - \u9ED8\u8BA4\u503C\u3002\u52A8\u753B\u5728\u6267\u884C\u4E4B\u524D\u6216\u4E4B\u540E\u4E0D\u4F1A\u5BF9\u5143\u7D20\u5E94\u7528\u4EFB\u4F55\u6837\u5F0F\u3002</li><li>forwards - \u5143\u7D20\u5C06\u4FDD\u7559\u7531\u6700\u540E\u4E00\u4E2A\u5173\u952E\u5E27\u8BBE\u7F6E\u7684\u6837\u5F0F\u503C\uFF08\u4F9D\u8D56 animation-direction \u548C animation-iteration-count\uFF09\u3002</li><li>backwards - \u5143\u7D20\u5C06\u83B7\u53D6\u7531\u7B2C\u4E00\u4E2A\u5173\u952E\u5E27\u8BBE\u7F6E\u7684\u6837\u5F0F\u503C\uFF08\u53D6\u51B3\u4E8E animation-direction\uFF09\uFF0C\u5E76\u5728\u52A8\u753B\u5EF6\u8FDF\u671F\u95F4\u4FDD\u7559\u8BE5\u503C\u3002</li><li>both - \u52A8\u753B\u4F1A\u540C\u65F6\u9075\u5FAA\u5411\u524D\u548C\u5411\u540E\u7684\u89C4\u5219\uFF0C\u4ECE\u800C\u5728\u4E24\u4E2A\u65B9\u5411\u4E0A\u6269\u5C55\u52A8\u753B\u5C5E\u6027\u3002</li></ul><h3 id="animation" tabindex="-1">animation <a class="header-anchor" href="#animation" aria-hidden="true">#</a></h3><p>animation \u5C5E\u6027\u662F\u4E00\u4E2A\u7B80\u5199\u5C5E\u6027\uFF0C\u7528\u4E8E\u8BBE\u7F6E\u516D\u4E2A\u52A8\u753B\u5C5E\u6027\uFF1A</p><p>\u8BED\u6CD5</p><div class="language-"><pre><code>animation: name duration timing-function delay iteration-count direction;
</code></pre></div><h3 id="animation-play-state" tabindex="-1">animation-play-state <a class="header-anchor" href="#animation-play-state" aria-hidden="true">#</a></h3><p>animation-play-state: paused|running; \u5C5E\u6027\u89C4\u5B9A\u52A8\u753B\u6B63\u5728\u6682\u505C\u8FD8\u662F\u8FD0\u884C\u3002</p><p>\u6CE8\u91CA\uFF1A\u60A8\u53EF\u4EE5\u5728 JavaScript \u4E2D\u4F7F\u7528\u8BE5\u5C5E\u6027\uFF0C\u8FD9\u6837\u5C31\u80FD\u5728\u64AD\u653E\u8FC7\u7A0B\u4E2D\u6682\u505C\u52A8\u753B\u3002</p>`,57),r=[l];function o(s,d,m,c,h,p){return n(),a("div",null,r)}var g=i(t,[["render",o]]);export{f as __pageData,g as default};
