import{_ as r,c as e,o as i,a as l}from"./app.ea3c2dda.js";const g='{"title":"\u8FB9\u6846","description":"","frontmatter":{},"headers":[{"level":2,"title":"border-style","slug":"border-style"},{"level":2,"title":"border-width","slug":"border-width"},{"level":2,"title":"border-color","slug":"border-color"},{"level":2,"title":"\u5355\u72EC\u7684\u8FB9","slug":"\u5355\u72EC\u7684\u8FB9"},{"level":2,"title":"border-radius","slug":"border-radius"},{"level":2,"title":"border-image","slug":"border-image"}],"relativePath":"cssdocs/cssBorder.md","lastUpdated":1641805504486}',d={},o=l(`<h1 id="\u8FB9\u6846" tabindex="-1">\u8FB9\u6846 <a class="header-anchor" href="#\u8FB9\u6846" aria-hidden="true">#</a></h1><p>border \u5C5E\u6027\u5141\u8BB8\u60A8\u6307\u5B9A\u5143\u7D20\u8FB9\u6846\u7684\u7C7B\u578B\u3001\u5BBD\u5EA6\u548C\u989C\u8272\u3002</p><p>border \u5C5E\u6027\u662F\u4EE5\u4E0B\u5404\u4E2A\u8FB9\u6846\u5C5E\u6027\u7684\u7B80\u5199\u5C5E\u6027\uFF1A</p><ul><li>border-width</li><li>border-style\uFF08\u5FC5\u9700\uFF09</li><li>border-color</li></ul><div class="language-"><pre><code>border: width style color;
</code></pre></div><p>\u8FD8\u53EF\u4EE5\u53EA\u4E3A\u4E00\u4E2A\u8FB9\u6307\u5B9A\u6240\u6709\u5355\u4E2A\u8FB9\u6846\u5C5E\u6027\uFF0C\u5982\uFF1A</p><div class="language-"><pre><code>border-left: 6px solid red;
</code></pre></div><h2 id="border-style" tabindex="-1">border-style <a class="header-anchor" href="#border-style" aria-hidden="true">#</a></h2><p>border-style \u5C5E\u6027\u6307\u5B9A\u8981\u663E\u793A\u7684\u8FB9\u6846\u7C7B\u578B\u3002</p><p>\u5141\u8BB8\u4EE5\u4E0B\u503C\uFF1A</p><ul><li>dotted - \u5B9A\u4E49\u70B9\u7EBF\u8FB9\u6846</li><li>dashed - \u5B9A\u4E49\u865A\u7EBF\u8FB9\u6846</li><li>solid - \u5B9A\u4E49\u5B9E\u7EBF\u8FB9\u6846</li><li>double - \u5B9A\u4E49\u53CC\u8FB9\u6846</li><li>groove - \u5B9A\u4E49 3D \u5761\u53E3\u8FB9\u6846\u3002\u6548\u679C\u53D6\u51B3\u4E8E border-color \u503C</li><li>ridge - \u5B9A\u4E49 3D \u810A\u7EBF\u8FB9\u6846\u3002\u6548\u679C\u53D6\u51B3\u4E8E border-color \u503C</li><li>inset - \u5B9A\u4E49 3D inset \u8FB9\u6846\u3002\u6548\u679C\u53D6\u51B3\u4E8E border-color \u503C</li><li>outset - \u5B9A\u4E49 3D outset \u8FB9\u6846\u3002\u6548\u679C\u53D6\u51B3\u4E8E border-color \u503C</li><li>none - \u5B9A\u4E49\u65E0\u8FB9\u6846</li><li>hidden - \u5B9A\u4E49\u9690\u85CF\u8FB9\u6846</li></ul><h2 id="border-width" tabindex="-1">border-width <a class="header-anchor" href="#border-width" aria-hidden="true">#</a></h2><p>border-width \u5C5E\u6027\u6307\u5B9A\u56DB\u4E2A\u8FB9\u6846\u7684\u5BBD\u5EA6\u3002</p><p>\u53EF\u4EE5\u5C06\u5BBD\u5EA6\u8BBE\u7F6E\u4E3A\u7279\u5B9A\u5927\u5C0F\uFF08\u4EE5 px\u3001pt\u3001cm\u3001em \u8BA1\uFF09\uFF0C\u4E5F\u53EF\u4EE5\u4F7F\u7528\u4EE5\u4E0B\u4E09\u4E2A\u9884\u5B9A\u4E49\u503C\u4E4B\u4E00\uFF1Athin\u3001medium \u6216 thick\uFF1A</p><h2 id="border-color" tabindex="-1">border-color <a class="header-anchor" href="#border-color" aria-hidden="true">#</a></h2><p>border-color \u5C5E\u6027\u7528\u4E8E\u8BBE\u7F6E\u56DB\u4E2A\u8FB9\u6846\u7684\u989C\u8272\u3002</p><ul><li>name - \u6307\u5B9A\u989C\u8272\u540D\uFF0C\u6BD4\u5982 &quot;red&quot;</li><li>HEX - \u6307\u5B9A\u5341\u516D\u8FDB\u5236\u503C\uFF0C\u6BD4\u5982 &quot;#ff0000&quot;</li><li>RGB - \u6307\u5B9A RGB \u503C\uFF0C\u6BD4\u5982 &quot;rgb(255,0,0)&quot;</li><li>HSL - \u6307\u5B9A HSL \u503C\uFF0C\u6BD4\u5982 &quot;hsl(0, 100%, 50%)&quot;</li><li>transparent</li></ul><p>\u6CE8\u91CA\uFF1A\u5982\u679C\u672A\u8BBE\u7F6E border-color\uFF0C\u5219\u5B83\u5C06\u7EE7\u627F\u5143\u7D20\u7684\u989C\u8272\u3002</p><h2 id="\u5355\u72EC\u7684\u8FB9" tabindex="-1">\u5355\u72EC\u7684\u8FB9 <a class="header-anchor" href="#\u5355\u72EC\u7684\u8FB9" aria-hidden="true">#</a></h2><p>\u53EF\u4EE5\u4E3A\u6BCF\u4E00\u4FA7\u6307\u5B9A\u4E0D\u540C\u7684\u8FB9\u6846\u3002</p><p>border-styl\u3001border-width\u3001border-color \u53EF\u4EE5\u8BBE\u7F6E\u4E00\u5230\u56DB\u4E2A\u503C\uFF08\u7528\u4E8E\u4E0A\u8FB9\u6846\u3001\u53F3\u8FB9\u6846\u3001\u4E0B\u8FB9\u6846\u548C\u5DE6\u8FB9\u6846\uFF09\u3002</p><ul><li>\u5C5E\u6027\u8BBE\u7F6E\u56DB\u4E2A\u503C\uFF1A\u5206\u522B\u662F\u4E0A\u3001\u53F3\u3001\u4E0B\u3001\u5DE6\uFF1B</li><li>\u5C5E\u6027\u8BBE\u7F6E\u4E09\u4E2A\u503C\uFF1A\u4E0A\u3001\u5DE6\u53F3\u3001\u4E0B\uFF1B</li><li>\u5C5E\u6027\u8BBE\u7F6E\u4E24\u4E2A\u503C\uFF1A\u4E0A\u4E0B\u3001\u5DE6\u53F3\uFF1B</li><li>\u5C5E\u6027\u8BBE\u7F6E\u4E00\u4E2A\u503C\uFF1A\u56DB\u6761\u8FB9\uFF1B</li></ul><p>\u6216\u8005\u53E6\u4E00\u79CD\u65B9\u5F0F\uFF0C\u5982\uFF1A</p><ul><li>border-top-style</li><li>border-right-style</li><li>border-bottom-style</li><li>border-left-style</li></ul><h2 id="border-radius" tabindex="-1">border-radius <a class="header-anchor" href="#border-radius" aria-hidden="true">#</a></h2><p>border-radius \u5C5E\u6027\u7528\u4E8E\u5411\u5143\u7D20\u6DFB\u52A0\u5706\u89D2\u8FB9\u6846\uFF1A</p><p>border-radius \u5C5E\u6027\u662F\u4E00\u4E2A\u7B80\u5199\u5C5E\u6027\uFF0C\u7528\u4E8E\u8BBE\u7F6E\u56DB\u4E2A border-*-radius \u5C5E\u6027\u3002</p><ul><li>border-top-left-radius</li><li>border-top-right-radius</li><li>border-bottom-right-radius</li><li>border-bottom-left-radius</li></ul><p>border-radius \u5C5E\u6027\u53EF\u4EE5\u63A5\u53D7\u4E00\u5230\u56DB\u4E2A\u503C\u3002\u89C4\u5219\u5982\u4E0B\uFF1A</p><ul><li>\u56DB\u4E2A\u503C\uFF1A\u4F9D\u6B21\u5206\u522B\u7528\u4E8E\uFF1A\u5DE6\u4E0A\u89D2\u3001\u53F3\u4E0A\u89D2\u3001\u53F3\u4E0B\u89D2\u3001\u5DE6\u4E0B\u89D2</li><li>\u4E09\u4E2A\u503C\uFF1A\u5DE6\u4E0A\u89D2\uFF0C\u53F3\u4E0A\u89D2\u548C\u5DE6\u4E0B\u89D2\uFF0C\u53F3\u4E0B\u89D2</li><li>\u4E24\u4E2A\u503C\uFF1A\u5DE6\u4E0A\u89D2\u548C\u53F3\u4E0B\u89D2\uFF0C\u53F3\u4E0A\u89D2\u548C\u5DE6\u4E0B\u89D2</li><li>\u4E00\u4E2A\u503C\uFF1A\u8BE5\u503C\u7528\u4E8E\u6240\u6709\u56DB\u4E2A\u89D2\uFF0C\u5706\u89D2\u90FD\u662F\u4E00\u6837\u7684</li></ul><h2 id="border-image" tabindex="-1">border-image <a class="header-anchor" href="#border-image" aria-hidden="true">#</a></h2><p>\u901A\u8FC7\u4F7F\u7528 border-image \u5C5E\u6027\uFF0C\u53EF\u4EE5\u8BBE\u7F6E\u56FE\u50CF\u7528\u4F5C\u56F4\u7ED5\u5143\u7D20\u7684\u8FB9\u6846\u3002</p><p>\u8BE5\u5C5E\u6027\u6709\u4E09\u90E8\u5206\uFF1A</p><ul><li>\u7528\u4F5C\u8FB9\u6846\u7684\u56FE\u50CF</li><li>\u5728\u54EA\u91CC\u88C1\u5207\u56FE\u50CF</li><li>\u5B9A\u4E49\u4E2D\u95F4\u90E8\u5206\u5E94\u91CD\u590D\u8FD8\u662F\u62C9\u4F38</li></ul><p>border-image \u5C5E\u6027\u5B9E\u9645\u4E0A\u662F\u4EE5\u4E0B\u5C5E\u6027\u7684\u7B80\u5199\u5C5E\u6027\uFF1A</p><ul><li>border-image-source \u89C4\u5B9A\u7528\u4F5C\u8FB9\u6846\u7684\u56FE\u50CF\u7684\u8DEF\u5F84\u3002</li><li>border-image-slice \u89C4\u5B9A\u5982\u4F55\u88C1\u5207\u8FB9\u6846\u56FE\u50CF\u3002</li><li>border-image-width \u89C4\u5B9A\u8FB9\u6846\u56FE\u50CF\u7684\u5BBD\u5EA6\u3002</li><li>border-image-outset \u89C4\u5B9A\u8FB9\u6846\u56FE\u50CF\u533A\u57DF\u8D85\u51FA\u8FB9\u6846\u76D2\u7684\u91CF\u3002</li><li>border-image-repeat \u89C4\u5B9A\u8FB9\u6846\u56FE\u50CF\u5E94\u91CD\u590D\u3001\u5706\u89D2\u3001\u8FD8\u662F\u62C9\u4F38\u3002</li></ul>`,36),t=[o];function a(s,b,h,u,c,p){return i(),e("div",null,t)}var _=r(d,[["render",a]]);export{g as __pageData,_ as default};
