import{_ as e,c as a,o as r,a as s}from"./app.ea3c2dda.js";const u='{"title":"CSS \u989C\u8272","description":"","frontmatter":{},"headers":[{"level":2,"title":"RGB \u989C\u8272","slug":"rgb-\u989C\u8272"},{"level":2,"title":"HEX \u989C\u8272","slug":"hex-\u989C\u8272"},{"level":2,"title":"HSL \u989C\u8272","slug":"hsl-\u989C\u8272"}],"relativePath":"cssdocs/cssColor.md","lastUpdated":1641805719620}',t={},l=s('<h1 id="css-\u989C\u8272" tabindex="-1">CSS \u989C\u8272 <a class="header-anchor" href="#css-\u989C\u8272" aria-hidden="true">#</a></h1><p>\u6307\u5B9A\u989C\u8272\u662F\u901A\u8FC7\u4F7F\u7528\u9884\u5B9A\u4E49\u7684\u989C\u8272\u540D\u79F0\uFF0C\u6216 RGB\u3001HEX\u3001HSL\u3001RGBA\u3001HSLA \u503C\u3002 \u53EF\u4EE5\u4E3A HTML \u5143\u7D20\u8BBE\u7F6E\u80CC\u666F\u8272\uFF0C\u6587\u672C\u7684\u989C\u8272\uFF0C\u8FB9\u6846\u7684\u989C\u8272\u7B49\u3002</p><p><a href="https://www.w3school.com.cn/cssref/css_colors.asp" target="_blank" rel="noopener noreferrer">CSS/HTML \u652F\u6301 140 \u79CD\u6807\u51C6\u989C\u8272\u540D</a></p><h2 id="rgb-\u989C\u8272" tabindex="-1">RGB \u989C\u8272 <a class="header-anchor" href="#rgb-\u989C\u8272" aria-hidden="true">#</a></h2><p>\u5728 CSS \u4E2D\uFF0C\u53EF\u4EE5\u4F7F\u7528\u4E0B\u9762\u7684\u516C\u5F0F\u5C06\u989C\u8272\u6307\u5B9A\u4E3A RGB \u503C\uFF1A<strong>rgb(red, green, blue)</strong></p><ul><li><p>\u6BCF\u4E2A\u53C2\u6570 (red\u3001green \u4EE5\u53CA blue) \u5B9A\u4E49\u4E86 0 \u5230 255 \u4E4B\u95F4\u7684\u989C\u8272\u5F3A\u5EA6\u3002</p></li><li><p>\u8981\u663E\u793A\u9ED1\u8272\uFF0C\u8BF7\u5C06\u6240\u6709\u989C\u8272\u53C2\u6570\u8BBE\u7F6E\u4E3A 0\uFF0C\u5982\u4E0B\u6240\u793A\uFF1Argb(0, 0, 0)\u3002</p></li><li><p>\u8981\u663E\u793A\u767D\u8272\uFF0C\u8BF7\u5C06\u6240\u6709\u989C\u8272\u53C2\u6570\u8BBE\u7F6E\u4E3A 255\uFF0C\u5982\u4E0B\u6240\u793A\uFF1Argb(255, 255, 255)\u3002</p></li></ul><p>RGBA \u989C\u8272\u503C\u662F\u5177\u6709 alpha \u901A\u9053\u7684 RGB \u989C\u8272\u503C\u7684\u6269\u5C55 - \u5B83\u6307\u5B9A\u4E86\u989C\u8272\u7684\u4E0D\u900F\u660E\u5EA6\u3002 RGBA \u989C\u8272\u503C\u6307\u5B9A\u4E3A\uFF1A<strong>rgba(red, green, blue, alpha)</strong></p><p>alpha \u53C2\u6570\u662F\u4ECB\u4E8E 0.0\uFF08\u5B8C\u5168\u900F\u660E\uFF09\u548C 1.0\uFF08\u5B8C\u5168\u4E0D\u900F\u660E\uFF09\u4E4B\u95F4\u7684\u6570\u5B57\u3002</p><h2 id="hex-\u989C\u8272" tabindex="-1">HEX \u989C\u8272 <a class="header-anchor" href="#hex-\u989C\u8272" aria-hidden="true">#</a></h2><p>\u5728 CSS \u4E2D\uFF0C\u53EF\u4EE5\u4F7F\u7528\u4EE5\u4E0B\u683C\u5F0F\u7684\u5341\u516D\u8FDB\u5236\u503C\u6307\u5B9A\u989C\u8272\uFF1A # rrggbb</p><p>\u5176\u4E2D rr\uFF08\u7EA2\u8272\uFF09\u3001gg\uFF08\u7EFF\u8272\uFF09\u548C bb\uFF08\u84DD\u8272\uFF09\u662F\u4ECB\u4E8E 00 \u548C ff \u4E4B\u95F4\u7684\u5341\u516D\u8FDB\u5236\u503C\uFF08\u4E0E\u5341\u8FDB\u5236 0-255 \u76F8\u540C\uFF09\u3002</p><p>\u4F8B\u5982\uFF0C#ff0000 \u663E\u793A\u4E3A\u7EA2\u8272\uFF0C\u56E0\u4E3A\u7EA2\u8272\u8BBE\u7F6E\u4E3A\u6700\u5927\u503C\uFF08ff\uFF09\uFF0C\u5176\u4ED6\u8BBE\u7F6E\u4E3A\u6700\u5C0F\u503C\uFF0800\uFF09\u3002</p><h2 id="hsl-\u989C\u8272" tabindex="-1">HSL \u989C\u8272 <a class="header-anchor" href="#hsl-\u989C\u8272" aria-hidden="true">#</a></h2><p>\u5728 CSS \u4E2D\uFF0C\u53EF\u4EE5\u4F7F\u7528\u8272\u76F8\u3001\u9971\u548C\u5EA6\u548C\u660E\u5EA6\uFF08HSL\uFF09\u6765\u6307\u5B9A\u989C\u8272\uFF0C\u683C\u5F0F\uFF1A<strong>hsla(hue, saturation, lightness)</strong></p><ul><li>\u8272\u76F8\uFF08hue\uFF09\u662F\u8272\u8F6E\u4E0A\u4ECE 0 \u5230 360 \u7684\u5EA6\u6570\u30020 \u662F\u7EA2\u8272\uFF0C120 \u662F\u7EFF\u8272\uFF0C240 \u662F\u84DD\u8272\u3002</li><li>\u9971\u548C\u5EA6\uFF08saturation\uFF09\u662F\u4E00\u4E2A\u767E\u5206\u6BD4\u503C\uFF0C0\uFF05 \u8868\u793A\u7070\u8272\u9634\u5F71\uFF0C\u800C 100\uFF05 \u662F\u5168\u8272\u3002</li><li>\u4EAE\u5EA6\uFF08lightness\uFF09\u4E5F\u662F\u767E\u5206\u6BD4\uFF0C0\uFF05 \u662F\u9ED1\u8272\uFF0C50\uFF05 \u662F\u65E2\u4E0D\u660E\u4E5F\u4E0D\u6697\uFF0C100\uFF05\u662F\u767D\u8272\u3002</li></ul><p>HSLA \u989C\u8272\u503C\u662F\u5E26\u6709 Alpha \u901A\u9053\u7684 HSL \u989C\u8272\u503C\u7684\u6269\u5C55 - \u5B83\u6307\u5B9A\u4E86\u989C\u8272\u7684\u4E0D\u900F\u660E\u5EA6\u3002 HSLA \u989C\u8272\u503C\u6307\u5B9A\u4E3A\uFF1A <strong>hsla(hue, saturation, lightness, alpha)</strong></p><p>alpha \u53C2\u6570\u662F\u4ECB\u4E8E 0.0\uFF08\u5B8C\u5168\u900F\u660E\uFF09\u548C 1.0\uFF08\u5B8C\u5168\u4E0D\u900F\u660E\uFF09\u4E4B\u95F4\u7684\u6570\u5B57\uFF1A</p>',17),h=[l];function n(p,i,o,c,d,g){return r(),a("div",null,h)}var S=e(t,[["render",n]]);export{u as __pageData,S as default};
